$(function(){"use strict";$("#contact-form .form-control").jqBootstrapValidation({preventSubmit:true,submitError:function($form,event,errors){},submitSuccess:function($form,event){$("#btn-submit").attr("disabled",true);event.preventDefault();var name=$("input#name").val(),email=$("input#email").val(),message=$("textarea#message").val(),firstName=name;if(firstName.indexOf(' ')>=0){firstName=name.split(' ').slice(0,-1).join(' ')}$.ajax({url:"./assets/php/contact-form.php",type:"POST",data:{name:name,email:email,message:message},cache:false,success:function(){$("#btn-submit").attr("disabled",false);$("#notification").html("<div class='alert alert-success'><button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;</button><strong>Your message has been sent.</strong></div>");$("#contact-form").trigger("reset")},error:function(){$("#btn-submit").attr("disabled",false);$("#notification").html("<div class='alert alert-danger'><button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;</button><strong>Sorry "+firstName+", it seems that my mail server is not responding. Please try again later!</strong></div>");$("#contact-form").trigger("reset")},})},filter:function(){return $(this).is(":visible")},});$("a[data-toggle=\"tab\"]").click(function(e){e.preventDefault();$(this).tab("show")});$('#name').focus(function(){$('#success').html('')})});